//picking one between 0 and 1

function randFix(){
    const choiceArr = [];
    for(let i = 0; i <1000; i++){
        choiceArr[i] = i%2==0 ? 1 : 0;
    }
    return choiceArr[Math.floor(Math.random() * 1000)];
}

export default randFix;
