    // this.setState({board, hasWon});
  }


  /** Render game board or winning message. */

  render() {

    return(
      <table className="Board">
        <tbody >
          <tr className="Board-row">
            <Cell isLit={true}/>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
          </tr>
          <tr>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
          </tr>
          <tr>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
          </tr>
          <tr>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
          </tr>
          <tr>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
            <Cell/>
          </tr>
        </tbody>
      </table>
    )
    // if the game is won, just show a winning msg & render nothing else

